<script>
import { mapActions, mapState } from 'pinia'
import { useGameStore } from '../stores/game'
export default {
  data() {
    return {
      gameStore: [],
      token: localStorage.access_token
    }
  },
  methods: {
    ...mapActions(useGameStore, ['addWishlist', 'findStore']),
    async fetchStores() {
      try {
        let fetch = await this.findStore(this.game.id)
        this.gameStore = this.stores
      } catch (error) {
        console.log(error)
      }
    },
    wishlistHandler() {
      let game = {
        gameName: this.game.name,
        id: this.game.id,
        imgUrl: this.game.background_image
      }
      this.addWishlist(game)
    }
  },
  computed: {
    ...mapState(useGameStore, ['game', 'stores']),
    formatDate() {
      const date = new Date(this.game.released)

      const year = date.getFullYear()
      const month = date.toLocaleString('default', { month: 'long' })
      const day = date.getDate()

      return `${day} ${month} ${year} `
    }
  },
  created() {
    this.fetchStores()
  }
}
</script>

